import React, { Component } from 'react';
import MyComponent from './MyComponent'

class App extends Component{
    constructor(props){
        super(props);

        this.state = {
            title: 'Initial Title',
            name: 'Initial name'
        };

        this.onClick = this.onClick.bind(this)
    }

    onClick(){
        this.setState({
            name: "New app name",
            title: "New app title"
        });
    }

    render(){
        return (
            <div className= "App">
                <input 
                    onChange={() => this.updateName}
                    vaule={this.state.name} />
            </div>
        )
    }
}

export default App;
