import React from 'react'
import ListItems from './ListItems'

class Form extends React.Component{
    constructor(props){
        super()
        this.state = {
           formOutPut : [1, 2, 3, 4],
           value: ''
        }

        const onFormSubmit = (e) => {
            e.preventDefault()

            this.props.onSubmit(this.state.formOutPut)
        }


    }
    render(){
        // const addValuesToState = () => {}

        return(
                    <form onSubmit= {this.onFormSubmit}>
                    {/* take text from import add to state */}
                    <input 
                        type="text" 
                        value={this.state.value}
                        onChange = {e => this.setState({ formOutPut : e.target.value})}
                    >
                    </input>
                    {/* apply output with map function */}                
                    <ul>
                        <ListItems itemContent ={this.state.formOutPut.value} />
                    </ul>
                </form>
            )       
    }
}

export default Form
