import React, { Component } from 'react';

class App extends Component{
    constructor(props){
        super(props);

        this.state = {
            title: 'App title',
            list: [1, 2, 3]
        };

        this.onClick = this.onClick.bind(this)
    }

    onClick(){
        this.setState({
            title: "New app title"
        });

    }
    render(){
        return (
            <div className= "App">
                <h1>{this.state.title}</h1>
                <div onClick={this.onClick}>click here!</div>
            </div>
        )
    }
}

export default App;
