import React from 'react'
import ListItems from './ListItems'

class Form extends React.Component{
    constructor(props){
        super()
        this.state = {formOutPut : ''}

        this.props.onSubmit(this.state.formOutPut)
    }
    render(){
        // const addValuesToState = () => {}

        return(
                <form>
                    {/* take text from import add to state */}
                    <input type="text" value={this.state.formOutPut}></input>
                    {/* apply output with map function */}                
                    <ul>
                        <ListItems itemContent ={this.state.formOutPut} />
                    </ul>
                </form>
            )       
    }
}

export default Form
