import React, { Component } from 'react';
import Instrument from './Instrument';

const instruments = [
    {
        name: 'mando',
        price: '150'
    },
    {
        name: 'violen',
        price: '155'
    }
];

localStorage.setItem('list', JSON.stringify(instruments))

class App extends Component{
    constructor(props){
        super(props)

        this.state = {
            instruments: JSON.parse(localStorage.getItem('instruments'))
        }

        this.onDelete = this.onDelete.bind(this);
    }
    componentWillMount(){
       const instruments = this.getInstruments()
       this.setState({ instruments });
    } 

    getInstruments(){
        return JSON.parse(localStorage.getItem('instruments'))        
    }

    onDelete(name) {
        const instruments = this.getInstruments();

        const filterInstruments = instruments.filter(instrument => {
            return instrument.name !== name;
        })

        this.setState({ instruments })     

    }

   render(){
       return (
           <div className="App">
               <h1> All Instrments </h1>
               {
               this.state.instruments.map(instrument => {
                   return (
                       <Instrument 
                            key={instrument.name}
                            {...instrument}
                            onDelete = {this.onDelete}
                       />
                   )
               })
            }        
            </div>
       )
   }
}

export default App;
