import React, { Component } from 'react';
import Instrument from './Instrument';

const instruments = [
    {
        name: 'mando',
        price: '150'
    },
    {
        name: 'violen',
        price: '155'
    }
];

localStorage.setItem('instruments', JSON.stringify(instruments))

class App extends Component{
    constructor(props){
        super(props)

        this.state = { 
            instruments: [1, 2, 3]
            // instruments : JSON.parse(localStorage.getItem(instruments))
        };

        this.onDelete = this.onDelete.bind(this);
        console.log('state', this.state);

        console.log("line 28",instruments);

    }

    componentWillMount(){
       const instruments = this.getInstruments()
    //    this.setState({ instruments });

    } 

    getInstruments(){
        return this.state.instruments;        
    }

    onDelete(name) {
        const instruments = this.getInstruments();

        const filterInstruments = instruments.filter(instrument => {
            return instrument.name !== name;
        })

        this.setState({ instruments: filterInstruments });     

    }

   render(){
       return (
           <div className="App">
                <h1> All Instrments </h1>
                {
                this.state.instruments.map(instrument => {
                   return (
                       <Instrument 
                            key={instrument.name}
                            name={instrument.name}
                            price = {instrument.price}
                            onDelete = {this.onDelete}
                       />
                   );
               })
            }         
            </div>


       )
   }
}

export default App;
