import React, { Component } from 'react';

const instruments = [
    {
        name: 'mando',
        price: '150'
    },
    {
        name: 'violen',
        price: '155'
    }
];

localStorage.setItem('list', JSON.stringify(instruments))

class App extends Component{
    constructor(props){
        super(props)

        this.state = {
            instruments: []
        }
    }
    componentWillMount(){
       const instrments =  JSON.parse(localStorage.getItem('instrument'))

       console.log(instrments);

       this.setState({ instruments });
    } 

   render(){
       return (
           <div className="App">
               <h1> Instrments </h1>
               {
               this.state.instruments.map(instrument => {
                   return (
                       <div key={instrument.name}>
                           <span>{instrument.name}></span> 
                           <span>{instrument.price}></span> 
                        </div>
                   )
               })
            }        
            </div>
       )
   }
}

export default App;
