import React, { Component } from 'react';
import Instrument from './Instrument';

const instruments = [
    {
        name: 'mando',
        price: '150'
    },
    {
        name: 'violen',
        price: '155'
    }
];

localStorage.setItem('list', JSON.stringify(instruments))

class App extends Component{
    constructor(props){
        super(props)

        this.state = {
            instruments: []
        }
    }
    componentWillMount(){
       this.getInstruments()
    } 

    getInstruments(){
        const instrments =  JSON.parse(localStorage.getItem('instrument'))

        console.log(instrments);

        this.setState({ instruments });
    }

   render(){
       return (
           <div className="App">
               <h1> Instrments </h1>
               {
               this.state.instruments.map(instrument => {
                   return (
                       <Instrument 
                        key={instrument.name}
                        {...instrument}
                       />
                   )
               })
            }        
            </div>
       )
   }
}

export default App;
